import { useState, useEffect } from 'react';
import TableRowData from './TableRowData';

const Perform = ()=>{

    const [data, setData] = useState([]);

    function fetchData(){

        fetch("https://blynk.cloud/external/api/get?token=UOkSf2Xaz1XFZbelzqZklo5E5lkJq-Ae&D1")
        .then((res) => res.json())
        .then((json) => {
            console.log(json);
            setData((data) => [...data,json]);
        });
    }

    function UpdateData(value){
        fetch("https://blynk.cloud/external/api/update?token=UOkSf2Xaz1XFZbelzqZklo5E5lkJq-Ae&V0=0.8")
    }
    return(
        <div className='Perform'>
          <div className="headPerform">Forward Bias Silicon Diode</div>
          <div className="controls">
              <h1>Controls</h1>
              <hr  className='ctrlHr'/>
              <div className="dcIn">
                <h4>DC volt : <span>0</span>V</h4>
                <input type="range" name="Voltage" />
              </div>
              <div className="btns">
                  <div className="btnsbtn" onClick={UpdateData}>Send to hardware</div>
                  <div className="btnsbtn" onClick={fetchData}>Add To Table</div>
                  <div className="btnsbtn">Plot</div>
                  <div className="btnsbtn" onClick={()=>setData([])}>Clear</div>
              </div>
          </div>
              <div className="ExpTable">
                  <table>
                      <tbody>
                      <tr>
                          <th>Forward Voltage(Volt)</th>
                          <th>Forward Current(mAmp)</th>
                      </tr>
                      {
                        data.map((data,num)=>(
                            <TableRowData data={data} key={num}/>
                        ))
                      }
                      </tbody>
                  </table>
              </div>
        </div>
    )
}

export default Perform;
