          {/* Render the cursor label */}
          <div
            style={{
              padding: "5px",
              fontSize: "10px",
              background: getBackgroundColor(dark),
              color: getBackgroundColor(!dark),
              borderRadius: "3px",
              position: "relative",
              transform: `translate3d(${alignPctX}%, ${alignPctY}%, 0)`,
              whiteSpace: "nowrap"
            }}
          >
            {renderedChildren}
          </div>
        </div>
      ) : null}
    </div>
  );
