    setChartState
  ] = React.useContext(ChartContext);

  const svgRef = React.useRef();

  React.useLayoutEffect(() => {
    if (!svgRef.current) {
      return;
    }
    const current = svgRef.current.getBoundingClientRect();
    if (current.left !== offset.left || current.top !== offset.top) {
      setChartState(state => ({
        ...state,
        offset: {
          left: current.left,
          top: current.top
        }
      }));
    }
  });

  const onMouseLeave = e => {
    setChartState(state => ({
      ...state,
      focused: null
    }));
    setChartState(state => ({
      ...state,
      pointer: {
        ...state.pointer,
        active: false
      }
    }));
  };

  const rafRef = React.useRef();

  const onMouseMove = e => {
    if (rafRef.current) {
      Raf.cancel(rafRef.current);
    }
    rafRef.current = Raf(() => {
      rafRef.current = null;
      const { clientX, clientY } = e;

      setChartState(state => {
        const x = clientX - offset.left - gridX;
        const y = clientY - offset.top - gridY;

        const pointer = {
          ...state.pointer,
          active: true,
          x,
          y,
          dragging: state.pointer && state.pointer.down
        };
        return {
          ...state,
          pointer
        };
      });
    });
  };

  const onMouseUp = () => {
    document.removeEventListener("mouseup", onMouseUp);
    document.removeEventListener("mousemove", onMouseMove);

    setChartState(state => ({
      ...state,
      pointer: {
        ...state.pointer,
        down: false,
        dragging: false,
        released: {
          x: state.pointer.x,
          y: state.pointer.y
        }
      }
    }));
  };

  const onMouseDown = () => {
    document.addEventListener("mouseup", onMouseUp);
    document.addEventListener("mousemove", onMouseMove);

    setChartState(state => ({
      ...state,
      pointer: {
        ...state.pointer,
        sourceX: state.pointer.x,
        sourceY: state.pointer.y,
        down: true
      }
    }));
  };

