import Utils from '../../utils/Utils'

import {
  groupingSingle,
  groupingSeries,
  groupingPrimary,
  groupingSecondary
} from '../../utils/Constants'

const defaultColors = [
  '#4ab5eb',
  '#fc6868',
  '#DECF3F',
  '#60BD68',
  '#FAA43A',
  '#c63b89',
  '#1aaabe',
  '#734fe9',
  '#1828bd',
  '#cd82ad'
