  primaryCursor,
  secondaryCursor,
  primaryAxes,
  secondaryAxes,
  focused,
  pointer,
  gridWidth,
  gridHeight,
  stackData,
}) => {
  return [primaryCursor, secondaryCursor].map((cursor, i) => {
    const cursorValue = cursor && cursor.value

    return React.useMemo(() => {
      if (!cursor) {
        return
